import React, { Component } from 'react';
import { Switch, Route } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import NewClient from './components/newclient';
import Customers from './components/customers';
import Search from './components/search';
import Navbar from './components/navbar';

class App extends Component {
  state = {
    users: [],
  }

  componentWillMount() {
    this.getUsers()
  }

  getUsers= () => {
    axios.get('http://localhost:5000/users')
      .then(res => {
        this.setState({
          users: res.data
        })
      })
  }

  getByName= () => {
    axios.get('http://localhost')
  }

  addUser = (user) => {
    axios.post('http://localhost:5000/users', user)
      .then(res => {
        console.log(res);
      })
  }

  findUser = (user) => {
    axios.get(`http://localhost:5000/users/${user}`)
  }

  render() {
    return (
      <div className="App">
        <div>
          <Navbar />

          <Switch>
            <Route exact path='/' render={() => <Customers users={this.state.users} />}></Route>
            <Route exact path='/newclient' render={() => <NewClient users={this.state.users} addUser={this.addUser}/>}></Route>
            <Route exact path='/search' render={() => <Search />}></Route>
          </Switch>
        </div>
      </div>
    );
  }
}

export default App;
