import React, { Component } from 'react';
// import './App.css';
import axios from 'axios';
import { Form, FormGroup,Label, Input } from 'reactstrap'
import { Animated } from 'react-animated-css'

const openWindow = () => {
  window.open()
}

class Contactclient extends Component {
  constructor() {
    super();
    this.state = {
        name: '',
        email: '',
        message: ''
        
    }
    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
}
handleChange = e => {
this.setState({ [ e.target.name ] : e.target.value })
}
 async handleSubmit(e){
  e.preventDefault()
  const { name, email, message } = this.state
  const form = await axios.post('/contactclient', {
    name, email, message
  })
}
  render() {
    return (
      <Animated animationIn="bounceInUp" animationOut="hinge" isVisible={true}>
        <div id="email-container" className="row">
          <Form method="POST" action="/contactclient" onSubmit={this.handleSubmit} style={{ width: '600px' }}>
            <FormGroup>
              <Label for="name">📛Name:</Label>
              <Input type="text"
                name="name"
                onChange={this.handleChange} />
            </FormGroup>
            <FormGroup>
              <Label for="email">📭Email:</Label>
              <Input type="text"
                name="email"
                onChange={this.handleChange} />
            </FormGroup>
            <FormGroup>
              <Label for="message">📧Message:</Label>
              <Input type="textarea"
                name="message"
                onChange={this.handleChange} />
            </FormGroup>
            <button type="submit" className="btn btn-primary">Submit</button>
          </Form>
          <button onClick={window.open('gmail.com')}></button>
        </div>
      </Animated>
    );
  }
}
export default Contactclient;
