        name: '',
        email: '',
        message: ''
    }

    openWindow = (e, u) => {
        window.open(`mailto:${u.email}?subject=${u.name}&body=${u.message}`)
    }

    sendMessage = (e) => {
        this.openWindow(e, this.state)
    }

