import React, { Component } from 'react'

class Edit extends Component {
    state = {
        users: {
            name: '',
            email: '',
            vehicle: '',
            tireRotation: '',
            oilChange: '',
            wheelAlign: '',
            brakePad: ''
        }
     }

     addUser = (e) => {
         console.log(this.state)
         this.props.addUser(this.state);
     }

     update = (e, id) => {
         console.log(id)
         this.props.update(id);
     }

     updateForm = (user) => {
         this.setState({
             name: user.name,
             email: user.email,
             vehicle: user.vehicle,
             tireRotation: i
         })
     }

     delete = (e, id) => {
         console.log(id)
         this.props.delete(id);
         window.location.reload();
     }

    render() {
     
        return(
            <div>
                <h1 id="newclient-hero-header">Register and Edit Clients</h1>
                <div className="container row">
                    <div className="col-md-6">
                        <form onSubmit={(e) => this.addUser(e)}>
                        {/* <form onSubmit={
                            if (adding a new user) {
                                (e) => this.addUser(e)
                            } else if (updating a user) {
                                (e) => this.update(e)
                            }
                        }> */}
                            <div className="form-group">
                                <label>Name</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    value={this.state.name}
                                    onChange={(e) => this.setState({ name: e.target.value })}
                                />
                            </div>

                            <div className="form-group">
                                <label>Email</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    value={this.state.email}
                                    onChange={(e) => this.setState({ email: e.target.value })}
                                />
                            </div>

                            <div className="form-group">
                                <label>Vehicle</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    value={this.state.vehicle}
                                    onChange={(e) => this.setState({ vehicle: e.target.value })}
                                />
                            </div>

                            <div className="form-group">
                                <label>Tire Rotation</label>
                                <input
                                    type="date"
                                    className="form-control"
                                    value={this.state.tireRotation}
                                    onChange={(e) => this.setState({ tireRotation: e.target.value })}
                                />
                            </div>

                            <div className="form-group">
                                <label>Oil Change</label>
                                <input
                                    type="date"
                                    className="form-control"
                                    value={this.state.oilChange}
                                    onChange={(e) => this.setState({ oilChange: e.target.value })}
                                />
                            </div>

                            <div className="form-group">
                                <label>Wheel Alignment</label>
                                <input
                                    type="date"
                                    className="form-control"
                                    value={this.state.wheelAlign}
                                    onChange={(e) => this.setState({ wheelAlign: e.target.value })}
                                />
                            </div>

                            <div className="form-group">
                                <label>Brake Pad</label>
                                <input
                                    type="date"
                                    className="form-control"
                                    value={this.state.brakePad}
                                    onChange={(e) => this.setState({ brakePad: e.target.value })}
                                />
                            </div>

                            <button type="submit" className="btn btn-primary">Submit</button>

                        </form>
                    </div>
                    <div className="col-md-6">
                        <table className="table table-hover">
                            <thead className="thead-dark">
                                <tr>
                                    <th scope="col">Name</th>
                                    <th scope="col">Vehicle</th>
                                    <th scope="col">Email</th>
                                    <th scope="col">Edit</th>
                                </tr>
                            </thead>
                            {
                                this.props.users.map((user, i) =>
                                <tbody key={i}>
                                    <tr>
                                        <td>{user.name}</td>
                                        <td>{user.vehicle}</td>
                                        <td>{user.email}</td>
                                        <td className="row">
                                            <button value="Refresh Page" className="btn btn-sm btn-danger" onClick={(e) => this.delete(e, user._id)}>X</button>
                                            <button className="btn btn-sm btn-primary" onClick={(e) => this.update(e, user._id)}>↺</button>
                                        </td>
                                    </tr>
                                </tbody>
                                )
                            }
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}

export default Edit;
